package com.cbe.mobile.banking.thirdparty.payment.integration.domain;

import static org.assertj.core.api.Assertions.assertThat;

public class ApiRequestAsserts {

    /**
     * Asserts that the entity has all properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertApiRequestAllPropertiesEquals(ApiRequest expected, ApiRequest actual) {
        assertApiRequestAutoGeneratedPropertiesEquals(expected, actual);
        assertApiRequestAllUpdatablePropertiesEquals(expected, actual);
    }

    /**
     * Asserts that the entity has all updatable properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertApiRequestAllUpdatablePropertiesEquals(ApiRequest expected, ApiRequest actual) {
        assertApiRequestUpdatableFieldsEquals(expected, actual);
        assertApiRequestUpdatableRelationshipsEquals(expected, actual);
    }

    /**
     * Asserts that the entity has all the auto generated properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertApiRequestAutoGeneratedPropertiesEquals(ApiRequest expected, ApiRequest actual) {
        assertThat(expected)
            .as("Verify ApiRequest auto generated properties")
            .satisfies(e -> assertThat(e.getId()).as("check id").isEqualTo(actual.getId()));
    }

    /**
     * Asserts that the entity has all the updatable fields set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertApiRequestUpdatableFieldsEquals(ApiRequest expected, ApiRequest actual) {
        assertThat(expected)
            .as("Verify ApiRequest relevant properties")
            .satisfies(e -> assertThat(e.getUri()).as("check uri").isEqualTo(actual.getUri()))
            .satisfies(e -> assertThat(e.getBody()).as("check body").isEqualTo(actual.getBody()))
            .satisfies(e -> assertThat(e.getMethod()).as("check method").isEqualTo(actual.getMethod()));
    }

    /**
     * Asserts that the entity has all the updatable relationships set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertApiRequestUpdatableRelationshipsEquals(ApiRequest expected, ApiRequest actual) {}
}
