package com.cbe.mobile.banking.thirdparty.payment.integration.domain;

import static org.assertj.core.api.Assertions.assertThat;

public class FormUiAsserts {

    /**
     * Asserts that the entity has all properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertFormUiAllPropertiesEquals(FormUi expected, FormUi actual) {
        assertFormUiAutoGeneratedPropertiesEquals(expected, actual);
        assertFormUiAllUpdatablePropertiesEquals(expected, actual);
    }

    /**
     * Asserts that the entity has all updatable properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertFormUiAllUpdatablePropertiesEquals(FormUi expected, FormUi actual) {
        assertFormUiUpdatableFieldsEquals(expected, actual);
        assertFormUiUpdatableRelationshipsEquals(expected, actual);
    }

    /**
     * Asserts that the entity has all the auto generated properties (fields/relationships) set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertFormUiAutoGeneratedPropertiesEquals(FormUi expected, FormUi actual) {
        assertThat(expected)
            .as("Verify FormUi auto generated properties")
            .satisfies(e -> assertThat(e.getId()).as("check id").isEqualTo(actual.getId()));
    }

    /**
     * Asserts that the entity has all the updatable fields set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertFormUiUpdatableFieldsEquals(FormUi expected, FormUi actual) {
        assertThat(expected)
            .as("Verify FormUi relevant properties")
            .satisfies(e -> assertThat(e.getFormName()).as("check formName").isEqualTo(actual.getFormName()))
            .satisfies(e -> assertThat(e.getFormDescription()).as("check formDescription").isEqualTo(actual.getFormDescription()))
            .satisfies(e -> assertThat(e.getFormType()).as("check formType").isEqualTo(actual.getFormType()))
            .satisfies(e -> assertThat(e.getStepOrder()).as("check stepOrder").isEqualTo(actual.getStepOrder()));
    }

    /**
     * Asserts that the entity has all the updatable relationships set.
     *
     * @param expected the expected entity
     * @param actual the actual entity
     */
    public static void assertFormUiUpdatableRelationshipsEquals(FormUi expected, FormUi actual) {
        assertThat(expected)
            .as("Verify FormUi relationships")
            .satisfies(
                e -> assertThat(e.getThirdPartyIntegration()).as("check thirdPartyIntegration").isEqualTo(actual.getThirdPartyIntegration())
            );
    }
}
